package com.example.filesharing.Auth;import jakarta.ws.rs.container.*;import jakarta.ws.rs.core.Response;import jakarta.ws.rs.ext.Provider;@Provider@PreMatchingpublic class AuthFilter implements ContainerRequestFilter {    private static final String AUTH_TOKEN = "secret123";    @Override    public void filter(ContainerRequestContext requestContext) {        String path = requestContext.getUriInfo().getPath();        if (path.startsWith("upload")) {            String auth = requestContext.getHeaderString("Authorization");            if (auth == null || !auth.equals("Bearer " + AUTH_TOKEN)) {                requestContext.abortWith(Response.status(Response.Status.UNAUTHORIZED)                        .entity("Unauthorized: valid Bearer token required").build());            }        }    }}