package com.example.filesharing.Filter;import jakarta.ws.rs.container.*;import jakarta.ws.rs.core.Response;import jakarta.ws.rs.ext.Provider;import java.io.IOException;@Provider@PreMatchingpublic class AuthFilter implements ContainerRequestFilter {    @Override    public void filter(ContainerRequestContext requestContext) throws IOException {        if ("OPTIONS".equalsIgnoreCase(requestContext.getMethod())) {            return;        }        String auth = requestContext.getHeaderString("Authorization");        if (auth == null || !auth.equals("Bearer secret123")) {            requestContext.abortWith(Response.status(Response.Status.UNAUTHORIZED)                    .entity("Unauthorized: valid Bearer token required")                    .build());        }    }}