package com.example.filesharing.resource;import com.example.filesharing.Model.Storage;import com.example.filesharing.Model.FileInfo;import jakarta.ws.rs.*;import jakarta.ws.rs.core.Response;@Path("/stats")public class StatsResource {    @GET    @Path("/{token}")    @Produces("application/json")    public Response getStats(@PathParam("token") String token) {        FileInfo info = Storage.downloadMap.get(token);        if (info == null) {            return Response.status(Response.Status.NOT_FOUND).entity("Invalid token").build();        }        return Response.ok(info).build();    }}